const fs = require('fs');
const path = require('path');
const dotenv = require('dotenv');

// Load environment variables from .env.local
const envPath = path.resolve(__dirname, '../.env.local');
const envConfig = dotenv.config({ path: envPath }).parsed;

if (!envConfig) {
    console.error('Error: Could not find .env.local file');
    process.exit(1);
}

const swContent = `/**
 * Firebase Cloud Messaging Service Worker
 * Handles background push notifications
 * 
 * NOTE: This file is auto-generated by scripts/generate-firebase-sw.js
 * Do not edit directly.
 */

importScripts('https://www.gstatic.com/firebasejs/10.7.1/firebase-app-compat.js');
importScripts('https://www.gstatic.com/firebasejs/10.7.1/firebase-messaging-compat.js');

// Firebase configuration
firebase.initializeApp({
    apiKey: '${envConfig.NEXT_PUBLIC_FIREBASE_API_KEY}',
    authDomain: '${envConfig.NEXT_PUBLIC_FIREBASE_AUTH_DOMAIN}',
    projectId: '${envConfig.NEXT_PUBLIC_FIREBASE_PROJECT_ID}',
    storageBucket: '${envConfig.NEXT_PUBLIC_FIREBASE_STORAGE_BUCKET}',
    messagingSenderId: '${envConfig.NEXT_PUBLIC_FIREBASE_MESSAGING_SENDER_ID}',
    appId: '${envConfig.NEXT_PUBLIC_FIREBASE_APP_ID}',
});

const messaging = firebase.messaging();

// Handle background messages
messaging.onBackgroundMessage((payload) => {
    console.log('[firebase-messaging-sw] Background message:', payload);

    const notificationTitle = payload.notification?.title || 'BestDeal';
    const notificationOptions = {
        body: payload.notification?.body || 'You have a new notification',
        icon: '/logo.png',
        badge: '/logo.png',
        tag: payload.data?.type || 'default',
        data: payload.data,
    };

    self.registration.showNotification(notificationTitle, notificationOptions);
});

// Handle notification click
self.addEventListener('notificationclick', (event) => {
    console.log('[firebase-messaging-sw] Notification clicked:', event);
    event.notification.close();

    // Get the URL from notification data or use default
    const urlToOpen = event.notification.data?.url || '/';

    event.waitUntil(
        clients.matchAll({ type: 'window', includeUncontrolled: true }).then((clientList) => {
            // Check if there's already a window open
            for (const client of clientList) {
                if (client.url.includes(self.location.origin) && 'focus' in client) {
                    client.navigate(urlToOpen);
                    return client.focus();
                }
            }
            // Open new window if none found
            if (clients.openWindow) {
                return clients.openWindow(urlToOpen);
            }
        })
    );
});

console.log('[firebase-messaging-sw] Service worker loaded');
`;

const swPath = path.resolve(__dirname, '../public/firebase-messaging-sw.js');
fs.writeFileSync(swPath, swContent);

console.log('Generated public/firebase-messaging-sw.js with environment variables');
